import {retrieveData,storeData} from "../utilies/localStorage";

export const fetchArticles = () => dispatch => {  
  const articles = retrieveData('articles');
  dispatch({
    type: 'FETCH_ARTICLES',
    payload : articles
  });
};

export const insertArticles = (article,user_id) => dispatch => {  
  const articles = retrieveData('articles');
  article.id = Math.max(articles.map(i => parseInt(i.id))) + 1;
  article.created_by = user_id;
  articles.push(article);

  storeData(articles);

  dispatch({
    type: 'ADD_NEW_ARTICLE',
    article : article
  });
};
