import React from "react";
import {
  BrowserRouter as Router,
  Redirect,
  Route,
  Link
} from "react-router-dom";
export default class DeleteArticle extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      redirectToHome : false
    }
  }
  handleOnDelete = event =>{
    this.props.deleteEvent(this.article.id);
    this.setState({
      redirectToHome : true
    })
  }
  render() {
    return (
      <React.Fragment>
        { redirectToHome &&  <Redirect to={`/`}/> }
        <h2>
          Are you sure you want to delete "{this.props.article.title}"?
        </h2>
        <button onClick={this.handleOnDelete}>Yes</button> <Link to={`/article/${this.props.article.id}`}>Cancel</Link>
        <hr />
      </React.Fragment>
    );
  }
}
